import React from "react";

export default class CardCreator extends React.Component {
  constructor() {
    super();
    this.state = {
      cardName: "",
      cardEffect: "",
      cardType: "red",
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  }

  render() {
    return (
      <div>
        <form className="cardForm"></form>
        <input
          type="text"
          name="cardName"
          placeholder="Bobby Scarnewman"
          onChange={this.handleChange}
          value={this.state.cardName}
        />
        <textarea
          name="cardEffect"
          placeholder="Bobby punches the sky with rage"
          onChange={this.handleChange}
          value={this.state.cardEffect}
          rows="10"
        />
        <select
          value={this.state.cardType}
          name="cardType"
          onChange={this.handleChange}
        >
          <option>Unit</option>
          <option>Invention</option>
          <option>Energy</option>
        </select>
      </div>
    );
  }
}
