import React from "react";

export default class CardCreator extends React.Component {
  constructor() {
    super();
    this.state = {
      cardName: "",
      cardEffect: "",
      cardType: "Unit",
      cardColor: "",
      cardPower: "",
      cardHP: "",
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  }

  render() {
    return (
      <div>
        <form className="cardForm">
          <label>Card Name</label>
          <input
            type="text"
            name="cardName"
            placeholder="Bobby Scarnewman"
            onChange={this.handleChange}
            value={this.state.cardName}
          />
          <br />
          <label>Card Text</label>
          <textarea
            name="cardEffect"
            placeholder="Bobby punches the sky with rage"
            onChange={this.handleChange}
            value={this.state.cardEffect}
            rows="10"
          />
          <br />
          <label>Card Type</label>
          <select
            value={this.state.cardType}
            name="cardType"
            onChange={this.handleChange}
          >
            <option>Unit</option>
            <option>Invention</option>
            <option>Energy</option>
          </select>
          <br />

          {this.cardType === unit && (
            <input
              type="text"
              name="attackPower"
              placeholder="1"
              onChange={this.handleChange}
              value={this.state.cardPower}
            />
          )}

          <label>Card Fuel</label>
          <select
            value={this.state.cardColor}
            name="cardColor"
            onChange={this.handleChange}
          >
            <option value="wind">Wind</option>
            <option value="electric">Electric</option>
            <option value="oil">Oil</option>
            <option value="sunlight">Sunlight</option>
            <option value="water">Water</option>
          </select>
          <br />
        </form>
      </div>
    );
  }
}
