import React from "react";
import CardForm from "../Components/CardForm";

export default class CardCreator extends React.Component {
  constructor() {
    super();
    this.state = {
      cardName: "",
      cardEffect: "",
      cardType: "unit",
      cardColor: "",
      cardPower: "",
      cardHP: "",
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  }

  render() {
    return <div></div>;
  }
}
